import{_ as a,o as e,c as r,e as n}from"./app.205a3d76.js";const i={},o=n('<h1 id="\u524D\u8A00" tabindex="-1"><a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a> \u524D\u8A00</h1><blockquote><p>\u672C\u4E66\u57FA\u4E8E Java 8 \u7248\u672C\u6765\u6559\u6388\u5F53\u524D Java \u7F16\u7A0B\u7684\u6700\u4F18\u5B9E\u8DF5\u3002</p></blockquote><p>\u6211\u4E4B\u524D\u7684 Java \u4E66\u7C4D <em>Thinking in Java, 4th Edition</em>\uFF08\u300AJava\u7F16\u7A0B\u601D\u60F3 (\u7B2C4\u7248)\u300B Prentice Hall 2006\uFF09\u4F9D\u7136\u9002\u7528\u4E8E Java 5 \u7F16\u7A0B\uFF0C\u5728\u6B64\u7248\u672C Java \u8BED\u8A00\u5F00\u59CB\u7528\u4F5C Android \u7F16\u7A0B\u3002\u6B64\u540E\uFF0C\u8FD9\u95E8\u8BED\u8A00\u7684\u8BB8\u591A\u5730\u65B9\u53D1\u751F\u4E86\u7FFB\u5929\u8986\u5730\u7684\u53D8\u5316\uFF0C\u7279\u522B\u662F Java 8 \u7684\u8F6C\u53D8\uFF0C\u4EE5\u81F3\u4E8E\u65B0\u7684 Java \u4EE3\u7801\u8BFB\u8D77\u6765\u7684\u611F\u89C9\u4E5F\u4E0D\u5C3D\u76F8\u540C\u3002\u8FD9\u4E5F\u4FC3\u4F7F\u6211\u65F6\u9694\u591A\u5E74\uFF0C\u521B\u4F5C\u4E86\u8FD9\u672C\u65B0\u4E66\u3002</p><p>\u300AOn Java 8\u300B\u65E8\u5728\u9762\u5411\u5DF2\u5177\u6709\u7F16\u7A0B\u57FA\u7840\u7684\u5F00\u53D1\u8005\u4EEC\u3002\u5BF9\u4E8E\u521D\u5B66\u8005\uFF0C\u53EF\u4EE5\u5148\u5728 <a href="http://Code.org" target="_blank" rel="noopener noreferrer">Code.org</a> \u6216\u8005 <a href="https://www.khanacademy.org/computing/computer-programming" target="_blank" rel="noopener noreferrer">\u53EF\u6C57\u5B66\u9662Khan Academy</a> \u7B49\u7F51\u7AD9\u8865\u5145\u5FC5\u8981\u7684\u9884\u5907\u77E5\u8BC6\u3002\u540C\u65F6\uFF0C<a href="http://www.OnJava8.com" target="_blank" rel="noopener noreferrer">OnJava8.com</a> \u4E0A\u4E5F\u6709\u514D\u8D39\u7684 Thinking in C\uFF08\u300AC\u7F16\u7A0B\u601D\u60F3\u300B\uFF09\u4E13\u9898\u77E5\u8BC6\u3002\u4E0E\u51E0\u5E74\u524D\u6211\u4EEC\u4F9D\u8D56\u5370\u5237\u5A92\u4F53\u65F6\u76F8\u6BD4\uFF0C\u50CF YouTube\u3001\u535A\u5BA2\u548C StackOverFlow \u8FD9\u6837\u7684\u7F51\u7AD9\u4F7F\u5F97\u5BFB\u627E\u7B54\u6848\u53D8\u5F97\u975E\u5E38\u7B80\u5355\u3002\u5982\u679C\u5C06\u672C\u4E66\u4F5C\u4E3A\u7F16\u7A0B\u5165\u95E8\u4E66\u7C4D\uFF0C\u8BF7\u7ED3\u5408\u8FD9\u4E9B\u5B66\u4E60\u9014\u5F84\u52AA\u529B\u575A\u6301\u4E0B\u53BB\u3002\u540C\u65F6\uFF0C\u672C\u4E66\u4E5F\u9002\u5408\u60F3\u8981\u6269\u5C55\u77E5\u8BC6\u7684\u4E13\u4E1A\u7A0B\u5E8F\u5458\u3002</p><p>\u5F97\u76CA\u4E8E\u300A<em>Thinking in Java</em>\u300B\uFF0C\u6211\u5F97\u4EE5\u5230\u4E16\u754C\u5404\u5730\u6F14\u8BB2\uFF0C\u6211\u5BF9\u6B64\u7531\u8877\u611F\u6FC0\u3002\u5B83\u4E3A\u6211\u7684 <a href="http://www.reinventing-business.com" target="_blank" rel="noopener noreferrer">Reinventing Business</a> \u9879\u76EE\u5728\u4E0E\u4EBA\u5458\u53CA\u516C\u53F8\u5EFA\u7ACB\u8054\u7CFB\u65B9\u9762\u63D0\u4F9B\u4E86\u5B9D\u8D35\u7684\u5E2E\u52A9\u3002\u6211\u6700\u7EC8\u64B0\u5199\u672C\u4E66\u7684\u539F\u56E0\u4E4B\u4E00\u5C31\u662F\u60F3\u652F\u6301\u8FD9\u4E00\u9879\u76EE\u7684\u7814\u7A76\uFF0C\u800C\u4E0B\u4E00\u4E2A\u5408\u4E4E\u903B\u8F91\u7684\u6B65\u9AA4\u4F3C\u4E4E\u662F\u5B9E\u9645\u521B\u5EFA\u4E00\u4E2A\u6240\u8C13\u7684\u84DD\u7EFF\u8272\u7EC4\u7EC7\uFF08Teal Organization\uFF09\u3002\u6211\u5E0C\u671B\u672C\u4E66\u53EF\u4EE5\u6210\u4E3A\u8BE5\u9879\u76EE\u7684\u4E00\u79CD\u4F17\u7B79\u3002</p><h2 id="\u6559\u5B66\u76EE\u6807" tabindex="-1"><a class="header-anchor" href="#\u6559\u5B66\u76EE\u6807" aria-hidden="true">#</a> \u6559\u5B66\u76EE\u6807</h2><p>\u6BCF\u7AE0\u8BB2\u6388\u4E00\u4E2A\u6216\u4E00\u7EC4\u76F8\u5173\u7684\u6982\u5FF5\uFF0C\u5E76\u4E14\u4E0D\u4F9D\u8D56\u4E8E\u5C1A\u672A\u4ECB\u7ECD\u7684\u7279\u6027\u3002\u8FD9\u6837\u4F60\u5C31\u53EF\u4EE5\u5728\u73B0\u6709\u7684\u77E5\u8BC6\u80CC\u666F\u4E0B\uFF0C\u5728\u7EE7\u7EED\u5411\u524D\u5B66\u4E60\u4E4B\u524D\uFF0C\u6D88\u5316\u5438\u6536\u6BCF\u4E00\u90E8\u5206\u5185\u5BB9\u3002</p><p>\u672C\u4E66\u7684\u6559\u5B66\u76EE\u6807\uFF1A</p><ol><li><p>\u5FAA\u5E8F\u6E10\u8FDB\u5730\u5448\u73B0\u5B66\u4E60\u5185\u5BB9\uFF0C\u4F7F\u8BFB\u8005\u53EF\u4EE5\u5F88\u5BB9\u6613\u5730\u5C06\u6BCF\u4E2A\u77E5\u8BC6\u70B9\u878D\u4F1A\u8D2F\u901A\uFF1B\u540C\u65F6\u4ED4\u7EC6\u5730\u5BF9\u7279\u6027\u7684\u8BB2\u89E3\u8FDB\u884C\u6392\u5E8F\uFF0C\u4EE5\u4F7F\u5F97\u4F60\u5728\u770B\u5230\u5BF9\u67D0\u4E2A\u7279\u6027\u7684\u8FD0\u7528\u4E4B\u524D\uFF0C\u4F1A\u5148\u4E86\u89E3\u5B83\u3002\u5982\u679C\u786E\u6709\u5FC5\u8981\u5F15\u5165\u8FD8\u672A\u5B66\u4E60\u7684\u77E5\u8BC6\u6982\u5FF5\uFF0C\u6211\u4F1A\u505A\u4E2A\u7B80\u77ED\u7684\u4ECB\u7ECD\u3002</p></li><li><p>\u4F7F\u7528\u5C3D\u53EF\u80FD\u7B80\u5355\u548C\u7B80\u77ED\u7684\u793A\u4F8B\uFF0C\u65B9\u4FBF\u8BFB\u8005\u7406\u89E3\u3002\u800C\u4E0D\u5F3A\u6C42\u5F15\u5165\u89E3\u51B3\u201C\u73B0\u5B9E\u4E16\u754C\u201D\u4E2D\u95EE\u9898\u7684\u4F8B\u5B50\u3002\u56E0\u4E3A\u6211\u53D1\u73B0\uFF0C\u901A\u5E38\u521D\u5B66\u8005\u66F4\u4E50\u4E8E\u770B\u5230\u81EA\u5DF1\u901A\u6653\u793A\u4F8B\u7684\u6BCF\u4E2A\u7EC6\u8282\uFF0C\u800C\u975E\u660E\u6670\u6240\u89E3\u51B3\u7684\u95EE\u9898\u8303\u7574\u3002\u6216\u8BB8\u6211\u4F1A\u56E0\u8FD9\u4E9B\u201C\u73A9\u5177\u793A\u4F8B\u201D\u800C\u88AB\u4E00\u4E9B\u4EBA\u6240\u8BDF\u75C5\uFF0C\u4F46\u662F\u6211\u4E50\u610F\u63A5\u53D7\u90A3\u4E9B\u6709\u5229\u4E8E\u4E3A\u6559\u80B2\u5E26\u6765\u76CA\u5904\u7684\u79CD\u79CD\u4E8B\u7269\uFF0C\u66F4\u5E0C\u671B\u8BFB\u8005\u4EEC\u80FD\u56E0\u6B64\u4FDD\u6301\u9976\u6709\u5174\u8DA3\u5730\u5B66\u4E60\u3002</p></li><li><p>\u5411\u8BFB\u8005\u63D0\u4F9B\u201C\u6211\u8BA4\u4E3A\u5BF9\u7406\u89E3\u8FD9\u79CD\u7A0B\u5E8F\u8BBE\u8BA1\u8BED\u8A00\u6765\u8BF4\u5F88\u91CD\u8981\u201D\u7684\u90E8\u5206\uFF0C\u800C\u4E0D\u662F\u63D0\u4F9B\u6211\u6240\u77E5\u9053\u7684\u6240\u6709\u4E8B\u60C5\u3002\u6211\u8BA4\u4E3A\u4FE1\u606F\u7684\u91CD\u8981\u6027\u662F\u5206\u5C42\u6B21\u7ED3\u6784\u7684\u3002\u7EDD\u5927\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u6211\u4EEC\u6CA1\u5FC5\u8981\u5F04\u6E05\u95EE\u9898\u7684\u6240\u6709\u672C\u8D28\u3002\u597D\u6BD4\u7F16\u7A0B\u8BED\u8A00\u4E2D\u7684\u67D0\u4E9B\u7279\u6027\u548C\u5B9E\u73B0\u7EC6\u8282\uFF0C95% \u7684\u7A0B\u5E8F\u5458\u90FD\u4E0D\u9700\u8981\u53BB\u77E5\u9053\u3002\u8FD9\u4E9B\u7EC6\u8282\u9664\u4E86\u4F1A\u52A0\u91CD\u4F60\u7684\u5B66\u4E60\u6210\u672C\uFF0C\u8FD8\u8BA9\u4F60\u66F4\u89C9\u5F97\u8FD9\u95E8\u8BED\u8A00\u597D\u590D\u6742\u3002\u5982\u679C\u4F60\u975E\u8981\u8003\u8651\u8FD9\u4E9B\u7EC6\u8282\uFF0C\u90A3\u4E48\u5B83\u8FD8\u4F1A\u8FF7\u60D1\u8BE5\u4EE3\u7801\u7684\u9605\u8BFB\u8005/\u7EF4\u62A4\u8005\uFF0C\u6240\u4EE5\u6211\u4E3B\u5F20\u9009\u62E9\u7B80\u5355\u7684\u65B9\u6CD5\u89E3\u51B3\u95EE\u9898\u3002</p></li><li><p>\u5E0C\u671B\u672C\u4E66\u80FD\u4E3A\u4F60\u6253\u4E0B\u575A\u5B9E\u7684\u57FA\u7840\uFF0C\u65B9\u4FBF\u4F60\u5C06\u6765\u5B66\u4E60\u66F4\u96BE\u7684\u8BFE\u7A0B\u548C\u4E66\u7C4D\u3002</p></li></ol><h2 id="\u8BED\u8A00\u8BBE\u8BA1\u9519\u8BEF" tabindex="-1"><a class="header-anchor" href="#\u8BED\u8A00\u8BBE\u8BA1\u9519\u8BEF" aria-hidden="true">#</a> \u8BED\u8A00\u8BBE\u8BA1\u9519\u8BEF</h2><p>\u6BCF\u79CD\u8BED\u8A00\u90FD\u6709\u8BBE\u8BA1\u9519\u8BEF\u3002\u5F53\u65B0\u624B\u7A0B\u5E8F\u5458\u6D89\u8DB3\u8BED\u8A00\u7279\u6027\u5E76\u731C\u6D4B\u5E94\u7528\u573A\u666F\u548C\u4F7F\u7528\u65B9\u5F0F\u65F6\uFF0C\u4ED6\u4EEC\u4F53\u9A8C\u5230\u6781\u5927\u7684\u4E0D\u786E\u5B9A\u6027\u548C\u632B\u6298\u611F\u3002\u627F\u8BA4\u9519\u8BEF\u4EE4\u4EBA\u5C34\u5C2C\uFF0C\u4F46\u8FD9\u79CD\u7CDF\u7CD5\u7684\u521D\u5B66\u8005\u7ECF\u5386\u6BD4\u8BA4\u8BC6\u5230\u4F60\u9519\u5728\u54EA\u91CC\u8FD8\u8981\u7CDF\u7CD5\u3002\u5509\uFF0C\u6BCF\u4E00\u79CD\u8BED\u8A00/\u5E93\u7684\u8BBE\u8BA1\u9519\u8BEF\u90FD\u4F1A\u6C38\u4E45\u5730\u5D4C\u5165\u5728 Java \u7684\u53D1\u884C\u7248\u4E2D\u3002</p><p>\u8BFA\u8D1D\u5C14\u7ECF\u6D4E\u5B66\u5956\u5F97\u4E3B\u7EA6\u745F\u592B\xB7\u65AF\u8482\u683C\u5229\u8328\uFF08<em>Joseph Stiglitz</em>\uFF09\u6709\u4E00\u5957\u9002\u7528\u4E8E\u8FD9\u91CC\u7684\u4EBA\u751F\u54F2\u5B66\uFF0C\u53EB\u505A\u201C\u627F\u8BFA\u5347\u7EA7\u7406\u8BBA\u201D\uFF1A\u7EE7\u7EED\u72AF\u9519\u8BEF\u7684\u6210\u672C\u7531\u522B\u4EBA\u627F\u62C5\uFF0C\u800C\u627F\u8BA4\u9519\u8BEF\u7684\u6210\u672C\u7531\u81EA\u5DF1\u627F\u62C5\u3002</p><p>\u770B\u8FC7\u6211\u6B64\u524D\u4F5C\u54C1\u7684\u8BFB\u8005\u4EEC\u5E94\u8BE5\u6E05\u695A\uFF0C\u6211\u4E00\u822C\u503E\u5411\u4E8E\u6307\u51FA\u8FD9\u4E9B\u9519\u8BEF\u3002Java \u62E5\u6709\u4E00\u6279\u72C2\u70ED\u7684\u7C89\u4E1D\u3002\u4ED6\u4EEC\u628A\u8BED\u8A00\u5F53\u6210\u662F\u9635\u8425\u800C\u4E0D\u662F\u7EAF\u7CB9\u7684\u7F16\u7A0B\u5DE5\u5177\u3002\u6211\u5199\u8FC7 Java \u4E66\u7C4D\uFF0C\u6240\u4EE5\u4ED6\u4EEC\u5140\u81EA\u8BA4\u4E3A\u6211\u81EA\u7136\u4E5F\u662F\u8FD9\u4E2A\u201C\u9635\u8425\u201D\u7684\u4E00\u4EFD\u5B50\u3002\u5F53\u6211\u6307\u51FA Java \u7684\u8FD9\u4E9B\u9519\u8BEF\u65F6\uFF0C\u4F1A\u9020\u6210\u4E24\u79CD\u5F71\u54CD\uFF1A</p><ol><li><p>\u65E9\u5148\u8BB8\u591A\u9519\u8BEF\u201C\u9635\u8425\u201D\u7684\u4EBA\u6210\u4E3A\u4E86\u727A\u7272\u54C1\u3002\u6700\u7EC8\uFF0C\u65F6\u9694\u591A\u5E74\u540E\uFF0C\u5927\u5BB6\u90FD\u610F\u8BC6\u5230\u8FD9\u662F\u4E2A\u8BBE\u8BA1\u4E0A\u7684\u9519\u8BEF\u3002\u7136\u800C\u9519\u8BEF\u5DF2\u7136\u6210\u4E3A Java \u5386\u53F2\u7684\u4E00\u90E8\u5206\u4E86\u3002</p></li><li><p>\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u65B0\u624B\u7A0B\u5E8F\u5458\u5E76\u6CA1\u6709\u7ECF\u5386\u8FC7\u201C\u8BED\u8A00\u4E3A\u4F55\u91C7\u7528\u67D0\u79CD\u65B9\u5F0F\u5B9E\u73B0\u201D\u7684\u4E89\u8BAE\u8FC7\u7A0B\u3002\u7279\u522B\u662F\u90A3\u4E9B\u9690\u7EA6\u5BDF\u89C9\u4E0D\u5BF9\u5374\u4F9D\u7136\u8BF4\u670D\u81EA\u5DF1\u201C\u6211\u5FC5\u987B\u8981\u8FD9\u4E48\u505A\u201D\u6216\u201C\u6211\u53EA\u662F\u6CA1\u5B66\u660E\u767D\u201D\u4ECE\u800C\u7EE7\u7EED\u9519\u4E0B\u53BB\u7684\u4EBA\u3002\u66F4\u7CDF\u7CD5\u7684\u662F\uFF0C\u6559\u6388\u8FD9\u4E9B\u7F16\u7A0B\u77E5\u8BC6\u7684\u8001\u5E08\u4EEC\u6CA1\u80FD\u6DF1\u5165\u5730\u53BB\u7814\u7A76\u8FD9\u91CC\u662F\u5426\u6709\u8BBE\u8BA1\u4E0A\u7684\u9519\u8BEF\uFF0C\u800C\u662F\u7EE7\u7EED\u9519\u8BEF\u7684\u89E3\u8BFB\u3002\u603B\u4E4B\uFF0C\u901A\u8FC7\u4E86\u89E3\u8BED\u8A00\u8BBE\u8BA1\u4E0A\u7684\u9519\u8BEF\uFF0C\u80FD\u8BA9\u5F00\u53D1\u8005\u4EEC\u66F4\u597D\u5730\u7406\u89E3\u548C\u610F\u8BC6\u5230\u9519\u8BEF\u7684\u672C\u8D28\uFF0C\u4ECE\u800C\u66F4\u5FEB\u5730\u8FDB\u6B65\u3002</p></li></ol><p>\u5BF9\u7F16\u7A0B\u8BED\u8A00\u7684\u8BBE\u8BA1\u9519\u8BEF\u7406\u89E3\u81F3\u5173\u91CD\u8981\uFF0C\u751A\u81F3\u5F71\u54CD\u7A0B\u5E8F\u5458\u7684\u5F00\u53D1\u6548\u7387\u3002\u90E8\u5206\u516C\u53F8\u5728\u5F00\u53D1\u8FC7\u7A0B\u4E2D\u907F\u514D\u4F7F\u7528\u8BED\u8A00\u7684\u67D0\u4E9B\u529F\u80FD\u7279\u6027\u3002\u8FD9\u4E9B\u529F\u80FD\u7279\u6027\u8868\u9762\u4E0A\u770B\u8D77\u6765\u9AD8\u5927\u4E0A\uFF0C\u4F46\u662F\u5F04\u4E0D\u597D\u5374\u53EF\u80FD\u51FA\u73B0\u610F\u6599\u4E4B\u5916\u7684\u9519\u8BEF\uFF0C\u5F71\u54CD\u6574\u4E2A\u5F00\u53D1\u8FDB\u7A0B\u3002</p><p>\u5DF2\u77E5\u7684\u8BED\u8A00\u8BBE\u8BA1\u9519\u8BEF\u4F1A\u7ED9\u65B0\u7684\u4E00\u95E8\u7F16\u7A0B\u8BED\u8A00\u7684\u4F5C\u8005\u63D0\u4F9B\u53C2\u8003\u3002\u63A2\u7D22\u4E00\u95E8\u8BED\u8A00\u80FD\u505A\u4EC0\u4E48\u662F\u5F88\u6709\u8DA3\u7684\u4E00\u4EF6\u4E8B\uFF0C\u800C\u8BED\u8A00\u8BBE\u8BA1\u9519\u8BEF\u80FD\u63D0\u9192\u4F60\u54EA\u4E9B\u201C\u5751\u201D\u662F\u4E0D\u80FD\u518D\u8D9F\u7684\u3002\u591A\u5E74\u4EE5\u6765\uFF0C\u6211\u4E00\u76F4\u611F\u89C9 Java \u7684\u8BBE\u8BA1\u8005\u4EEC\u6709\u70B9\u8131\u79BB\u7FA4\u4F17\u3002Java \u7684\u6709\u4E9B\u8BBE\u8BA1\u9519\u8BEF\u9519\u7684\u592A\u660E\u663E\uFF0C\u6211\u751A\u81F3\u6000\u7591\u8BBE\u8BA1\u8005\u4EEC\u5230\u5E95\u662F\u4E3A\u51FA\u4E8E\u670D\u52A1\u7528\u6237\u8FD8\u662F\u5176\u4ED6\u52A8\u673A\u8BBE\u8BA1\u4E86\u8FD9\u4E9B\u529F\u80FD\u3002Java \u8BED\u8A00\u6709\u8BB8\u591A\u81ED\u540D\u662D\u8457\u7684\u8BBE\u8BA1\u9519\u8BEF\uFF0C\u5F88\u53EF\u80FD\u8FD9\u4E5F\u662F\u8BF1\u60D1\u6240\u5728\u3002Java \u4F3C\u4E4E\u5E76\u4E0D\u5C0A\u91CD\u5F00\u53D1\u8005\u3002\u4E3A\u6B64\u6211\u5F88\u957F\u65F6\u95F4\u5185\u4E0D\u60F3\u4E0E Java \u6709\u4EFB\u4F55\u74DC\u845B\u3002\u5F88\u5927\u7A0B\u5EA6\u4E0A\uFF0C\u8FD9\u4E5F\u662F\u6211\u4E0D\u60F3\u78B0 Java \u7684\u539F\u56E0\u5427\u3002</p><p>\u5982\u4ECA\u518D\u5BA1\u89C6 Java 8\uFF0C\u6211\u53D1\u73B0\u4E86\u8BB8\u591A\u53D8\u5316\u3002\u8BBE\u8BA1\u8005\u4EEC\u5BF9\u4E8E\u8BED\u8A00\u548C\u7528\u6237\u7684\u6001\u5EA6\u4F3C\u4E4E\u53D1\u751F\u4E86\u6839\u672C\u6027\u4E0A\u7684\u6539\u53D8\u3002\u5FFD\u89C6\u7528\u6237\u6295\u8BC9\u591A\u5E74\u4E4B\u540E\uFF0CJava \u7684\u8BB8\u591A\u529F\u80FD\u548C\u7C7B\u5E93\u90FD\u5DF2\u88AB\u641E\u7838\u4E86\u3002</p><p>\u65B0\u529F\u80FD\u7684\u8BBE\u8BA1\u4E0E\u4EE5\u5F80\u6709\u5F88\u5927\u4E0D\u540C\u3002\u638C\u8235\u8005\u5F00\u59CB\u91CD\u89C6\u7A0B\u5E8F\u5458\u7684\u7F16\u7A0B\u7ECF\u9A8C\u3002\u65B0\u529F\u80FD\u7684\u5F00\u53D1\u90FD\u662F\u5728\u52AA\u529B\u4F7F\u8BED\u8A00\u53D8\u5F97\u66F4\u597D\uFF0C\u800C\u975E\u4EC5\u4EC5\u505C\u7559\u5728\u5FEB\u901F\u5806\u780C\u529F\u80FD\u800C\u4E0D\u53BB\u6DF1\u5165\u7814\u7A76\u5B83\u4EEC\u7684\u542B\u4E49\u3002\u751A\u81F3\u6709\u4E9B\u65B0\u7279\u6027\u7684\u5B9E\u73B0\u65B9\u5F0F\u975E\u5E38\u4F18\u96C5\uFF08\u81F3\u5C11\u5728 Java \u7EA6\u675F\u4E0B\u5C3D\u53EF\u80FD\u4F18\u96C5\uFF09\u3002</p><p>\u6211\u731C\u6D4B\u53EF\u80FD\u662F\u90E8\u5206\u8BBE\u8BA1\u8005\u7684\u79BB\u5F00\u8BA9\u4ED6\u4EEC\u610F\u8BC6\u5230\u4E86\u8FD9\u70B9\u3002\u8BF4\u771F\u7684\uFF0C\u6211\u6CA1\u60F3\u5230\u4F1A\u6709\u8FD9\u4E9B\u53D8\u5316\uFF01\u56E0\u4E3A\u8FD9\u4E9B\u539F\u56E0\uFF0C\u5199\u8FD9\u672C\u4E66\u7684\u4F53\u9A8C\u8981\u6BD4\u4EE5\u5F80\u597D\u5F88\u591A\u3002Java 8 \u5305\u542B\u4E86\u4E00\u7CFB\u5217\u57FA\u7840\u548C\u91CD\u8981\u7684\u6539\u8FDB\u3002\u9057\u61BE\u7684\u662F\uFF0C\u4E3A\u4E86\u4E25\u683C\u5730\u201C\u5411\u540E\u517C\u5BB9\u201D\uFF0C\u6211\u4EEC\u4E0D\u5927\u53EF\u80FD\u770B\u5230\u620F\u5267\u6027\u7684\u53D8\u5316\uFF0C\u5F53\u7136\u6211\u5E0C\u671B\u6211\u662F\u9519\u7684\u3002\u5C3D\u7BA1\u5982\u6B64\uFF0C\u6211\u5F88\u8D5E\u8D4F\u90A3\u4E9B\u6562\u4E8E\u81EA\u6211\u98A0\u8986\uFF0C\u5E76\u4E3A Java \u8BBE\u5B9A\u66F4\u597D\u8DEF\u7EBF\u7684\u4EBA\u3002\u7B2C\u4E00\u6B21\uFF0C\u5BF9\u4E8E\u81EA\u5DF1\u6240\u5199\u7684\u90E8\u5206 Java 8 \u4EE3\u7801\u6211\u7EC8\u4E8E\u53EF\u4EE5\u8BF4\u201C\u8D5E\uFF01\u201D</p><p>\u6700\u540E\uFF0C\u672C\u4E66\u6240\u8457\u65F6\u95F4\u4F3C\u4E4E\u4E5F\u8FD8\u4E0D\u9519\uFF0C\u56E0\u4E3A Java 8 \u5F15\u5165\u7684\u65B0\u529F\u80FD\u5DF2\u7ECF\u5F3A\u70C8\u5730\u5F71\u54CD\u4E86\u4ECA\u540E Java \u7684\u7F16\u7801\u65B9\u5F0F\u3002\u622A\u6B62\u6211\u5728\u5199\u8FD9\u672C\u4E66\u65F6\uFF0CJava 9 \u4F3C\u4E4E\u66F4\u4E13\u6CE8\u4E8E\u5BF9\u8BED\u8A00\u5E95\u5C42\u7684\u57FA\u7840\u7ED3\u6784\u529F\u80FD\u7684\u91CD\u8981\u66F4\u65B0\uFF0C\u800C\u975E\u672C\u4E66\u6240\u5173\u6CE8\u7684\u65B0\u7F16\u7801\u65B9\u5F0F\u3002\u8BDD\u8BF4\u56DE\u6765\uFF0C\u5F97\u76CA\u4E8E\u7535\u5B50\u4E66\u51FA\u7248\u5F62\u5F0F\u7684\u4FBF\u6377\uFF0C\u5047\u5982\u6211\u53D1\u73B0\u672C\u4E66\u6709\u9700\u8981\u66F4\u65B0\u6216\u6DFB\u52A0\u7684\u5185\u5BB9\uFF0C\u6211\u53EF\u4EE5\u7B2C\u4E00\u65F6\u95F4\u5C06\u65B0\u7248\u672C\u63A8\u9001\u7ED9\u73B0\u6709\u8BFB\u8005\u3002</p><h2 id="\u6D4B\u8BD5\u7528\u4F8B" tabindex="-1"><a class="header-anchor" href="#\u6D4B\u8BD5\u7528\u4F8B" aria-hidden="true">#</a> \u6D4B\u8BD5\u7528\u4F8B</h2><p>\u4E66\u4E2D\u4EE3\u7801\u793A\u4F8B\u57FA\u4E8E Java 8 \u548C Gradle \u7F16\u8BD1\u6784\u5EFA\uFF0C\u5E76\u4E14\u4EE3\u7801\u793A\u4F8B\u90FD\u4FDD\u5B58\u5728<a href="https://github.com/BruceEckel/OnJava8-Examples" target="_blank" rel="noopener noreferrer">\u8FD9\u4E2A\u81EA\u7531\u8BBF\u95EE\u7684GitHub\u7684\u4ED3\u5E93</a> \u4E2D\u3002\u6211\u4EEC\u9700\u8981\u5185\u7F6E\u7684\u6D4B\u8BD5\u6846\u67B6\uFF0C\u4EE5\u4FBF\u4E8E\u5728\u6BCF\u6B21\u6784\u5EFA\u7CFB\u7EDF\u65F6\u81EA\u52A8\u8FD0\u884C\u3002\u5426\u5219\uFF0C\u4F60\u5C06\u65E0\u6CD5\u4FDD\u8BC1\u81EA\u5DF1\u4EE3\u7801\u7684\u53EF\u9760\u6027\u3002\u4E3A\u4E86\u5B9E\u73B0\u8FD9\u4E00\u70B9\uFF0C\u6211\u521B\u5EFA\u4E86\u4E00\u4E2A\u6D4B\u8BD5\u7CFB\u7EDF\u6765\u663E\u793A\u548C\u9A8C\u8BC1\u5927\u591A\u6570\u793A\u4F8B\u7684\u8F93\u51FA\u7ED3\u679C\u3002\u8FD9\u4E9B\u8F93\u51FA\u7ED3\u679C\u6211\u4F1A\u9644\u52A0\u5728\u793A\u4F8B\u7ED3\u5C3E\u7684\u4EE3\u7801\u5757\u4E2D\u3002\u6709\u65F6\u4EC5\u663E\u793A\u5FC5\u8981\u7684\u90A3\u51E0\u884C\u6216\u8005\u9996\u5C3E\u884C\u3002\u5229\u7528\u8FD9\u79CD\u65B9\u5F0F\u6765\u6539\u5584\u8BFB\u8005\u7684\u9605\u8BFB\u548C\u5B66\u4E60\u4F53\u9A8C\uFF0C\u540C\u65F6\u4E5F\u63D0\u4F9B\u4E86\u4E00\u79CD\u9A8C\u8BC1\u793A\u4F8B\u6B63\u786E\u6027\u7684\u65B9\u6CD5\u3002</p><h2 id="\u53D7\u6B22\u8FCE\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u53D7\u6B22\u8FCE\u5EA6" aria-hidden="true">#</a> \u53D7\u6B22\u8FCE\u5EA6</h2><p>Java\u7684\u53D7\u6B22\u8FCE\u7A0B\u5EA6\u5177\u6709\u91CD\u8981\u610F\u4E49\u3002\u5B66\u4E60 Java \u4F1A\u8BA9\u4F60\u66F4\u5BB9\u6613\u627E\u5230\u5DE5\u4F5C\u3002\u76F8\u5173\u7684\u57F9\u8BAD\u6750\u6599\uFF0C\u8BFE\u7A0B\u548C\u5176\u4ED6\u53EF\u7528\u7684\u5B66\u4E60\u8D44\u6E90\u4E5F\u5F88\u591A\u3002\u5BF9\u4E8E\u4F7F\u7528Java\u7684\u521D\u521B\u4F01\u4E1A\u6765\u8BF4\uFF0C\u62DB\u8058 Java \u7A0B\u5E8F\u5458\u76F8\u5BF9\u5BB9\u6613\u3002\u5982\u679C\u4F60\u771F\u7684\u4E0D\u559C\u6B22 Java \u8BED\u8A00\uFF0C\u90A3\u4E48\u6700\u597D\u4E0D\u8981\u4F7F\u7528\u5B83\u2014\u2014\u2014\u2014\u4EC5\u4EC5\u4E3A\u4E86\u627E\u5DE5\u4F5C\u800C\u4F7F\u7528\u5B83\uFF0C\u5E76\u4E0D\u662F\u4E00\u4E2A\u5FEB\u4E50\u7684\u751F\u6D3B\u9009\u62E9\u3002\u4F5C\u4E3A\u4E00\u5BB6\u516C\u53F8\uFF0C\u5728\u6280\u672F\u9009\u578B\u524D\u4E00\u5B9A\u4E0D\u8981\u5355\u5355\u53EA\u8003\u8651\u5BB9\u6613\u62DB\u8058Java \u7A0B\u5E8F\u5458\u3002\u6BCF\u79CD\u8BED\u8A00\u90FD\u6709\u5176\u9002\u7528\u7684\u8303\u56F4\uFF0C\u6709\u53EF\u80FD\u53E6\u4E00\u79CD\u7F16\u7A0B\u8BED\u8A00\u66F4\u9002\u7528\u4E8E\u4F60\u4EEC\u7684\u4E1A\u52A1\uFF0C\u6765\u8FBE\u5230\u4E8B\u534A\u529F\u500D\u7684\u6548\u679C\u3002\u5982\u679C\u4F60\u771F\u7684\u559C\u6B22 Java\uFF0C\u90A3\u4E48\u6B22\u8FCE\u4F60\u3002\u5E0C\u671B\u8FD9\u672C\u4E66\u80FD\u4E30\u5BCC\u4F60\u7684\u7F16\u7A0B\u7ECF\u9A8C\uFF01</p><h2 id="\u5173\u4E8E\u5B89\u5353" tabindex="-1"><a class="header-anchor" href="#\u5173\u4E8E\u5B89\u5353" aria-hidden="true">#</a> \u5173\u4E8E\u5B89\u5353</h2><p>\u672C\u4E66\u57FA\u4E8E Java 8 \u7248\u672C\u3002\u5982\u679C\u4F60\u662F Andriod \u7A0B\u5E8F\u5458\uFF0C\u8BF7\u52A1\u5FC5\u5B66\u4E60 Java 5\u3002\u5728\u300AOn Java 8\u300B\u51FA\u7248\u7684\u65F6\u5019\uFF0C\u6211\u7684\u53E6\u4E00\u672C\u57FA\u4E8E Java 5 \u7684\u8457\u4F5C <em>Thinking in Java 4th Edition</em>\uFF08\u300AJava\u7F16\u7A0B\u601D\u60F3\u300B\u7B2C\u56DB\u7248\uFF09\u5DF2\u7ECF\u53EF\u4EE5\u5728<a href="http://www.OnJava8.com" target="_blank" rel="noopener noreferrer">www.OnJava8.com</a>\u4E0A\u514D\u8D39\u4E0B\u8F7D\u4E86\u3002\u6B64\u5916\uFF0C\u8FD8\u6709\u8BB8\u591A\u5176\u4ED6\u4E13\u7528\u4E8E Andriod \u7F16\u7A0B\u7684\u8D44\u6E90\u3002</p><h2 id="\u7535\u5B50\u7248\u6743\u58F0\u660E" tabindex="-1"><a class="header-anchor" href="#\u7535\u5B50\u7248\u6743\u58F0\u660E" aria-hidden="true">#</a> \u7535\u5B50\u7248\u6743\u58F0\u660E</h2><p>\u300AOn Java 8\u300B\u4EC5\u63D0\u4F9B\u7535\u5B50\u7248\uFF0C\u5E76\u4E14\u4EC5\u901A\u8FC7 <a href="http://www.OnJava8.com" target="_blank" rel="noopener noreferrer">www.OnJava8.com</a> \u63D0\u4F9B\u3002\u4EFB\u4F55\u672A\u7ECF mindviewinc@gmail.com \u6388\u6743\u7684\u5176\u4ED6\u6765\u6E90\u6216\u6D41\u4F20\u9001\u673A\u6784\u90FD\u662F\u975E\u6CD5\u7684\u3002\u672C\u4F5C\u54C1\u53D7\u7248\u6743\u4FDD\u62A4\uFF01\u672A\u7ECF\u8BB8\u53EF\uFF0C\u8BF7\u52FF\u901A\u8FC7\u4EE5\u4EFB\u4F55\u65B9\u5F0F\u5206\u4EAB\u6216\u53D1\u5E03\u3002\u4F60\u53EF\u4EE5\u4F7F\u7528\u8FD9\u4E9B\u793A\u4F8B\u8FDB\u884C\u6559\u5B66\uFF0C\u53EA\u8981\u4E0D\u5BF9\u672C\u4E66\u975E\u6CD5\u91CD\u65B0\u51FA\u7248\u3002\u6709\u5173\u5B8C\u6574\u8BE6\u7EC6\u4FE1\u606F\uFF0C\u8BF7\u53C2\u9605\u793A\u4F8B\u5206\u53D1\u4E2D\u7684 Copyright.txt \u6587\u4EF6\u3002\u5BF9\u4E8E\u89C6\u89C9\u969C\u788D\u8005\uFF0C\u7535\u5B50\u7248\u672C\u6709\u53EF\u641C\u7D22\u6027\uFF0C\u5B57\u4F53\u5927\u5C0F\u8C03\u6574\u6216\u6587\u672C\u5230\u8BED\u97F3\u7B49\u8BF8\u591A\u597D\u5904\u3002</p><p>\u4EFB\u4F55\u8D2D\u4E70\u8FD9\u672C\u4E66\u7684\u8BFB\u8005\uFF0C\u8FD8\u9700\u8981\u4E00\u53F0\u8BA1\u7B97\u673A\u6765\u8FD0\u884C\u548C\u5199\u4F5C\u4EE3\u7801\u3002\u53E6\u5916\u7535\u5B50\u7248\u5728\u8BA1\u7B97\u673A\u4E0A\u548C\u79FB\u52A8\u8BBE\u5907\u4E0A\u7684\u663E\u793A\u6548\u679C\u4FF1\u4F73\uFF0C\u63A8\u8350\u4F7F\u7528\u5E73\u677F\u8BBE\u5907\u9605\u8BFB\u3002\u76F8\u6BD4\u8D2D\u4E70\u4F20\u7EDF\u7EB8\u8D28\u7248\u7684\u4EF7\u683C\uFF0C\u5E73\u677F\u7535\u8111\u4EF7\u683C\u90FD\u8DB3\u591F\u4FBF\u5B9C\u3002\u5728\u5E8A\u4E0A\u9605\u8BFB\u7535\u5B50\u7248\u6BD4\u770B\u8FD9\u6837\u4E00\u672C\u539A\u539A\u7684\u5B9E\u4F53\u4E66\u8981\u65B9\u4FBF\u5F97\u591A\u3002\u8D77\u521D\u4F60\u53EF\u80FD\u4F1A\u6709\u4E9B\u4E0D\u4E60\u60EF\uFF0C\u4F46\u6211\u76F8\u4FE1\u5F88\u5FEB\u4F60\u5C31\u4F1A\u53D1\u73B0\u5B83\u5E26\u6765\u7684\u4F18\u70B9\u8FDC\u80DC\u8FC7\u4E0D\u9002\u3002\u6211\u5DF2\u7ECF\u8D70\u8FC7\u8FD9\u4E2A\u9636\u6BB5\uFF0CGoogle Play \u56FE\u4E66\u7684\u6D4F\u89C8\u5668\u9605\u8BFB\u4F53\u9A8C\u975E\u5E38\u597D\uFF0C\u5305\u62EC\u5728 Linux \u548C iOS \u8BBE\u5907\u4E0A\u3002\u4F5C\u4E3A\u4E00\u6B21\u5C1D\u8BD5\uFF0C\u6211\u51B3\u5B9A\u5C1D\u8BD5\u901A\u8FC7 Google \u56FE\u4E66\u8FDB\u884C\u51FA\u7248\u3002</p><p><strong>\u6CE8\u610F</strong>\uFF1A\u5728\u64B0\u5199\u672C\u6587\u65F6\uFF0C\u901A\u8FC7 Google Play \u56FE\u4E66\u7F51\u7EDC\u6D4F\u89C8\u5668\u5E94\u7528\u9605\u8BFB\u56FE\u4E66\u867D\u7136\u53EF\u4EE5\u5FCD\u53D7\uFF0C\u4F46\u4F53\u9A8C\u8FD8\u662F\u6709\u70B9\u5DEE\u5F3A\u4EBA\u610F\uFF0C\u6211\u5F3A\u70C8\u63A8\u8350\u8BFB\u8005\u4EEC\u4F7F\u7528\u5E73\u677F\u7535\u8111\u6765\u9605\u8BFB\u3002</p><h2 id="\u7248\u672C\u8BF4\u660E" tabindex="-1"><a class="header-anchor" href="#\u7248\u672C\u8BF4\u660E" aria-hidden="true">#</a> \u7248\u672C\u8BF4\u660E</h2><p>\u672C\u4E66\u91C7\u7528 <a href="http://pandoc.org" target="_blank" rel="noopener noreferrer">Pandoc</a> \u98CE\u683C\u7684 Markdown \u7F16\u5199\uFF0C\u4F7F\u7528 Pandoc \u751F\u6210 ePub v3 \u683C\u5F0F\u3002</p><p>\u6B63\u6587\u5B57\u4F53\u4E3A Georgia\uFF0C\u6807\u9898\u5B57\u4F53\u4E3A Verdana\u3002 \u4EE3\u7801\u5B57\u4F53\u4F7F\u7528\u7684 Ubuntu Mono\uFF0C\u56E0\u4E3A\u5B83\u7279\u522B\u7D27\u51D1\uFF0C\u5355\u884C\u80FD\u5BB9\u7EB3\u66F4\u591A\u7684\u4EE3\u7801\u3002 \u6211\u9009\u62E9\u5C06\u4EE3\u7801\u5185\u8054\uFF08\u800C\u4E0D\u662F\u5C06\u5217\u8868\u653E\u5165\u56FE\u50CF\uFF0C\u53C2\u7167\u5176\u4ED6\u4E66\u7C4D\uFF09\uFF0C\u56E0\u4E3A\u6211\u89C9\u5F97\u8FD9\u4E2A\u529F\u80FD\u5F88\u91CD\u8981\uFF1A\u8BA9\u4EE3\u7801\u5757\u80FD\u9002\u5E94\u5B57\u4F53\u5927\u5C0F\u5F97\u6539\u53D8\u800C\u6539\u53D8\uFF08\u5426\u5219\uFF0C\u4E70\u7535\u5B50\u7248\uFF0C\u8FD8\u56FE\u4EC0\u4E48\u5462\uFF1F\uFF09\u3002</p><p>\u4E66\u4E2D\u7684\u63D0\u53D6\uFF0C\u7F16\u8BD1\u548C\u6D4B\u8BD5\u4EE3\u7801\u793A\u4F8B\u7684\u6784\u5EFA\u8FC7\u7A0B\u90FD\u662F\u81EA\u52A8\u5316\u7684\u3002\u6240\u6709\u81EA\u52A8\u5316\u64CD\u4F5C\u90FD\u662F\u901A\u8FC7\u6211\u5728 Python 3 \u4E2D\u7F16\u5199\u7684\u7A0B\u5E8F\u6765\u5B9E\u73B0\u7684\u3002</p><h2 id="\u5C01\u9762\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#\u5C01\u9762\u8BBE\u8BA1" aria-hidden="true">#</a> \u5C01\u9762\u8BBE\u8BA1</h2><p>\u300AOn Java 8\u300B\u7684\u5C01\u9762\u662F\u6839\u636E W.P.A.\uFF08Works Progress Administration 1935\u5E74\u81F31943\u5E74\u7F8E\u56FD\u5927\u8427\u6761\u671F\u95F4\u7684\u4E00\u4E2A\u5DE8\u5927\u9879\u76EE\uFF0C\u5B83\u4F7F\u6570\u767E\u4E07\u5931\u4E1A\u4EBA\u5458\u91CD\u65B0\u5C31\u4E1A\uFF09\u7684\u9A6C\u8D5B\u514B\u521B\u4F5C\u7684\u3002\u5B83\u8FD8\u8BA9\u6211\u60F3\u8D77\u4E86\u300A\u7EFF\u91CE\u4ED9\u8E2A\u300B\uFF08<em>The Wizard of Oz</em>\uFF09\u7CFB\u5217\u4E1B\u4E66\u4E2D\u7684\u63D2\u56FE\u3002 \u6211\u7684\u597D\u670B\u53CB\u3001\u8BBE\u8BA1\u5E08\u4E39 <em>Daniel Will-Harris</em>\uFF08<a href="http://www.will-harris.com" target="_blank" rel="noopener noreferrer">www.will-harris.com</a>\uFF09\u548C\u6211\u90FD\u559C\u6B22\u8FD9\u4E2A\u5F62\u8C61\u3002</p><h2 id="\u611F\u8C22\u7684\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u611F\u8C22\u7684\u4EBA" aria-hidden="true">#</a> \u611F\u8C22\u7684\u4EBA</h2><p>\u611F\u8C22 <em>Domain-Driven Design</em>\uFF08\u300A\u9886\u57DF\u9A71\u52A8\u8BBE\u8BA1\u300B \uFF09\u7684\u4F5C\u8005 <em>Eric Evans</em> \u5EFA\u8BAE\u4E66\u540D\uFF0C\u4EE5\u53CA\u5176\u4ED6\u65B0\u95FB\u7EC4\u6821\u5BF9\u7684\u5E2E\u52A9\u3002</p><p>\u611F\u8C22 <em>James Ward</em> \u4E3A\u6211\u5F00\u59CB\u4F7F\u7528 Gradle \u5DE5\u5177\u6784\u5EFA\u8FD9\u672C\u4E66\uFF0C\u4EE5\u53CA\u4ED6\u591A\u5E74\u6765\u7684\u5E2E\u52A9\u548C\u53CB\u8C0A\u3002</p><p>\u611F\u8C22 <em>Ben Muschko</em> \u5728\u6574\u7406\u6784\u5EFA\u6587\u4EF6\u65B9\u9762\u7684\u5DE5\u4F5C\uFF0C\u8FD8\u6709\u611F\u8C22 <em>Hans Dockter</em> \u7ED9 <em>Ben</em> \u63D0\u4F9B\u4E86\u65F6\u95F4\u3002</p><p>\u611F\u8C22 <em>Jeremy Cerise</em> \u548C <em>Bill Frasure</em> \u6765\u5230\u5F00\u53D1\u5546\u52A1\u805A\u4F1A\u9884\u8BA2\uFF0C\u5E76\u968F\u540E\u63D0\u4F9B\u4E86\u5B9D\u8D35\u7684\u5E2E\u52A9\u3002</p><p>\u611F\u8C22\u6240\u6709\u82B1\u65F6\u95F4\u548C\u7CBE\u529B\u6765\u79D1\u7F57\u62C9\u591A\u5DDE\u514B\u96F7\u65AF\u7279\u5FB7\u6BD4\u7279\uFF08Crested Butte, Colorado\uFF09\u9547\u53C2\u52A0\u6211\u7684\u7814\u8BA8\u4F1A\uFF0C\u5F00\u53D1\u5546\u52A1\u805A\u4F1A\u548C\u5176\u4ED6\u6D3B\u52A8\u7684\u4EBA\uFF01\u4F60\u4EEC\u7684\u8D21\u732E\u53EF\u80FD\u4E0D\u5BB9\u6613\u770B\u5230\uFF0C\u4F46\u5374\u975E\u5E38\u91CD\u8981\uFF01</p><h2 id="\u732E\u793C" tabindex="-1"><a class="header-anchor" href="#\u732E\u793C" aria-hidden="true">#</a> \u732E\u793C</h2><blockquote><p>\u8C28\u4EE5\u6B64\u4E66\u732E\u7ED9\u6211\u656C\u7231\u7684\u7236\u4EB2 E. Wayne Eckel (1924.4.1 ~ 2016.11.23)\u3002</p></blockquote><div style="page-break-after:always;"></div>',45),t=[o];function h(p,d){return e(),r("div",null,t)}var c=a(i,[["render",h],["__file","00-Preface.html.vue"]]);export{c as default};
