import{_ as a,o as e,c as p,e as t}from"./app.205a3d76.js";const o={},v=t('<p>[TOC]</p><h1 id="\u9644\u5F55-c-\u548Cjava\u7684\u4F18\u826F\u4F20\u7EDF" tabindex="-1"><a class="header-anchor" href="#\u9644\u5F55-c-\u548Cjava\u7684\u4F18\u826F\u4F20\u7EDF" aria-hidden="true">#</a> \u9644\u5F55:C++\u548CJava\u7684\u4F18\u826F\u4F20\u7EDF</h1><blockquote><p>\u5728\u5404\u79CD\u8BA8\u8BBA\u58F0\u4E2D\uFF0C\u6709\u4E00\u4E9B\u4EBA\u8BA4\u4E3AC++\u662F\u4E00\u79CD\u8BBE\u8BA1\u7CDF\u7CD5\u7684\u8BED\u8A00\u3002 \u6211\u8BA4\u4E3A\u7406\u89E3C++\u548CJava\u8BED\u8A00\u7684\u9009\u62E9\u6709\u52A9\u4E8E\u4E86\u89E3\u66F4\u5927\u7684\u89C6\u89D2\u3002</p></blockquote><p>\u4E5F\u5C31\u662F\u8BF4\uFF0C\u6211\u51E0\u4E4E\u4E0D\u518D\u4F7F\u7528C++\u4E86\u3002\u5F53\u6211\u4F7F\u7528\u5B83\u7684\u65F6\u5019\uFF0C\u8981\u4E48\u662F\u7528\u6765\u68C0\u67E5\u9057\u7559\u4EE3\u7801\uFF0C\u8981\u4E48\u662F\u7F16\u5199\u6027\u80FD\u5173\u952E\uFF08performance-critical\uFF09\u90E8\u5206\uFF0C\u7A0B\u5E8F\u901A\u5E38\u5C3D\u53EF\u80FD\u5C0F\uFF0C\u4EE5\u4FBF\u7528\u5176\u4ED6\u8BED\u8A00\u7F16\u5199\u7684\u5176\u4ED6\u7A0B\u5E8F\u6765\u8C03\u7528\u3002</p><p>\u56E0\u4E3A\u6211\u5728\u6700\u521D\u76848\u5E74\u91CC\u4E00\u76F4\u5728C++\u6807\u51C6\u59D4\u5458\u4F1A\u5DE5\u4F5C\uFF0C\u6240\u4EE5\u6211\u89C1\u8BC1\u4E86\u90A3\u4E9B\u88AB\u505A\u51FA\u7684\u51B3\u5B9A\u3002\u5B83\u4EEC\u90FD\u7ECF\u8FC7\u4E86\u6781\u5176\u8C28\u614E\u7684\u8003\u8651\uFF0C\u8FDC\u8FDC\u8D85\u8FC7\u4E86\u8BB8\u591A\u5728Java\u4E2D\u505A\u51FA\u7684\u51B3\u5B9A\u3002</p><p>\u7136\u800C\uFF0C\u6B63\u5982\u4EBA\u4EEC\u6B63\u786E\u5730\u6307\u51FA\u7684\u90A3\u6837\uFF0C\u7531\u6B64\u4EA7\u751F\u7684\u8BED\u8A00\u4F7F\u7528\u8D77\u6765\u65E2\u590D\u6742\u53C8\u75DB\u82E6\uFF0C\u800C\u4E14\u53EA\u8981\u6211\u4E00\u6BB5\u65F6\u95F4\u4E0D\u4F7F\u7528\u5B83\uFF0C\u6211\u5C31\u4F1A\u5FD8\u8BB0\u90A3\u4E9B\u53E4\u602A\u7684\u89C4\u5219\u3002\u5728\u6211\u5199\u4E66\u7684\u65F6\u5019\uFF0C\u6211\u662F\u4ECE\u7B2C\u4E00\u539F\u7406\uFF08first principles\uFF09\u5904\u4E86\u89E3\u8FD9\u4E9B\u89C4\u5219\u7684\uFF0C\u800C\u4E0D\u662F\u8BB0\u4F4F\u4E86\u5B83\u4EEC\u3002</p><p>\u4E3A\u4E86\u7406\u89E3C++\u8BED\u8A00\u4E3A\u4F55\u65E2\u4EE4\u4EBA\u4E0D\u6109\u5FEB\u4E14\u590D\u6742\uFF0C\u540C\u65F6\u53C8\u662F\u7CBE\u5FC3\u8BBE\u8BA1\u7684\uFF0C\u5FC5\u987B\u8981\u7262\u8BB0C++\u4E2D\u6240\u6709\u5185\u5BB9\u7684\u4E3B\u8981\u8BBE\u8BA1\u51B3\u7B56\uFF1A\u4E0EC. Bjarne Stroustrup\uFF08\u8BE5\u8BED\u8A00\u7684\u521B\u9020\u8005\uFF0C\u5373\u201CC++\u4E4B\u7236\u201D\uFF09\u7684\u517C\u5BB9\u6027\u51B3\u5B9A\u3002\u8FD9\u6837\u7684\u8BBE\u8BA1\u4F3C\u4E4E\u662F\u4E3A\u4E86\u53EF\u4EE5\u8BA9\u5927\u91CF\u7684C\u7A0B\u5E8F\u5458\u900F\u660E\u5730\u8F6C\u79FB\u5230\u5BF9\u8C61\uFF08\u4EE3\u6307C++\uFF09\u4E0A\uFF1A\u5141\u8BB8\u4ED6\u4EEC\u5728C++\u4E0B\u7F16\u8BD1\u4ED6\u4EEC\u7684C\u4EE3\u7801\u3002\u8FD9\u662F\u4E00\u4E2A\u5DE8\u5927\u7684\u9650\u5236\uFF0C\u4E00\u76F4\u662FC++\u6700\u5927\u7684\u4F18\u52BF......\u800C\u4E14\u4E5F\u662F\u5B83\u7684\u7978\u6839\u3002\u8FD9\u5C31\u662F\u4F7F\u5F97C++\u6210\u529F\u7684\u539F\u56E0\uFF0C\u4E5F\u662F\u4F7F\u5B83\u590D\u6742\u7684\u539F\u56E0\u3002</p><p>\u5B83\u4E5F\u6B3A\u9A97\u4E86\u90A3\u4E9B\u4E0D\u592A\u4E86\u89E3C++\u7684Java\u8BBE\u8BA1\u5E08\u3002\u4F8B\u5982\uFF0C\u4ED6\u4EEC\u8BA4\u4E3A\u8FD0\u7B97\u7B26\u91CD\u8F7D\u5BF9\u4E8E\u7A0B\u5E8F\u5458\u6765\u8BF4\u5F88\u96BE\u6B63\u786E\u4F7F\u7528\u3002\u8FD9\u5728C++\u4E2D\u57FA\u672C\u4E0A\u662F\u6B63\u786E\u7684\uFF0C\u56E0\u4E3AC++\u65E2\u6709\u6808\u5206\u914D\u53C8\u6709\u5806\u5206\u914D\uFF0C\u4F60\u5FC5\u987B\u91CD\u8F7D\u8FD0\u7B97\u7B26\u6765\u5904\u7406\u6240\u6709\u60C5\u51B5\u800C\u4E14\u4E0D\u8981\u9020\u6210\u5185\u5B58\u6CC4\u6F0F\u3002\u8FD9\u786E\u5B9E\u5F88\u56F0\u96BE\u3002\u7136\u800C\uFF0CJava\u6709\u5355\u4E00\u7684\u5185\u5B58\u5206\u914D\u673A\u5236\u548C\u4E00\u4E2A\u5783\u573E\u6536\u96C6\u5668\uFF0C\u8FD9\u4F7F\u5F97\u8FD0\u7B97\u7B26\u91CD\u8F7D\u53D8\u5F97\u5FAE\u4E0D\u8DB3\u9053\uFF0C\u6B63\u5982C\uFF03\u4E2D\u90A3\u6837\uFF08\u4F46\u5728\u65E9\u4E8EJava\u7684Python\u4E2D\u5DF2\u7ECF\u53EF\u4EE5\u770B\u5230\uFF09\u3002\u4F46\u591A\u5E74\u6765\uFF0C\u6765\u81EAJava\u56E2\u961F\u7684\u4E00\u8D2F\u6001\u5EA6\u662F\u201C\u8FD0\u7B97\u7B26\u91CD\u8F7D\u592A\u590D\u6742\u4E86\u201D\u3002\u8FD9\u91CC\u8FD8\u6709\u8BB8\u591A\u51B3\u7B56\uFF0C\u6240\u505A\u7684\u4E8B\u660E\u663E\u4E0D\u5E94\u8BE5\u662F\u4ED6\u4EEC\u505A\u7684\u3002\u6B63\u662F\u7531\u4E8E\u8FD9\u4E9B\u539F\u56E0\uFF0C\u8BA9\u6211\u6709\u4E86\u8511\u89C6Gosling\uFF08\u5373\u201CJava\u4E4B\u7236\u201D\uFF09\u548CJava\u56E2\u961F\u51B3\u7B56\u7684\u540D\u58F0\u3002\uFF08Java 7\u548C8\u7531\u4E8E\u67D0\u79CD\u539F\u56E0\u5305\u542B\u4E86\u66F4\u597D\u7684\u51B3\u7B56\u3002\u4F46\u662F\u5411\u540E\u517C\u5BB9\u6027\u8FD9\u4E2A\u7EA6\u675F\u603B\u662F\u4F1A\u963B\u788D\u771F\u6B63\u7684\u6539\u8FDB\u3002\u8BED\u8A00\u6C38\u8FDC\u4E0D\u4F1A\u662F\u5B83\u672C\u6765\u7684\u6837\u5B50\u3002\uFF09</p><p>\u8FD8\u6709\u5F88\u591A\u5176\u4ED6\u7684\u4F8B\u5B50\u3002\u201C\u4E3A\u4E86\u63D0\u9AD8\u6548\u7387\uFF0C\u5FC5\u987B\u5305\u542B\u57FA\u672C\u7C7B\u578B\u201D\uFF1B\u575A\u6301\u201C\u4E07\u7269\u7686\u5BF9\u8C61\u201D\u662F\u6B63\u786E\u7684\uFF1B\u5F53\u5BF9\u6027\u80FD\u6709\u8981\u6C42\u7684\u65F6\u5019\uFF0C\u63D0\u4F9B\u4E00\u4E2A\u9677\u9631\u95E8\uFF08trap door\uFF09\u6765\u505A\u4F4E\u7EA7\u522B\u7684\u6D3B\u52A8\uFF08lower-level activities\uFF09\uFF08\u8FD9\u91CC\u4E5F\u53EF\u4EE5\u4F7F\u7528hotspot\u6280\u672F\u900F\u660E\u5730\u63D0\u9AD8\u6027\u80FD\uFF0C\u6B63\u5982\u4ED6\u4EEC\u6700\u7EC8\u505A\u7684\u90A3\u6837\uFF09\uFF1B\u4E0D\u80FD\u76F4\u63A5\u4F7F\u7528\u6D6E\u70B9\u5904\u7406\u5668\u53BB\u8BA1\u7B97\u8D85\u8D8A\u51FD\u6570\uFF0C\u5B83\u7528\u8F6F\u4EF6\u6765\u5B8C\u6210\u3002\u6211\u5DF2\u7ECF\u5C3D\u53EF\u80FD\u591A\u5730\u63D0\u51FA\u4E86\u8FD9\u6837\u7684\u95EE\u9898\uFF0C\u4F46\u6211\u5F97\u5230\u7684\u5374\u4E00\u76F4\u662F\u7C7B\u4F3C\u201C\u8FD9\u662FJava\u65B9\u5F0F\u201D\u8FD9\u6837\u7684\u56DE\u590D\u3002</p><p>\u5F53\u6211\u63D0\u51FA\u5173\u4E8E\u6CDB\u578B\u7684\u8BBE\u8BA1\u6709\u591A\u7CDF\u7CD5\u7684\u65F6\u5019\uFF0C\u6211\u5F97\u5230\u4E86\u76F8\u540C\u7684\u56DE\u590D\uFF0C\u4EE5\u53CA\u201C\u6211\u4EEC\u5FC5\u987B\u5411\u540E\u517C\u5BB9\u90A3\u6837\u4EE5\u524D\u7528Java\u505A\u51FA\u7684\u51B3\u7B56\u201D\uFF08\u5373\u4F7F\u5B83\u4EEC\u662F\u7CDF\u7CD5\u7684\u51B3\u7B56\uFF09\u3002\u6700\u8FD1\u8D8A\u6765\u8D8A\u591A\u7684\u4EBA\u5DF2\u7ECF\u83B7\u5F97\u4E86\u8DB3\u591F\u7684\u6CDB\u578B\u7ECF\u9A8C\uFF0C\u53EF\u4EE5\u53D1\u73B0\u6CDB\u578B\u771F\u7684\u5F88\u96BE\u7528\u3002\u4E8B\u5B9E\u4E0A\uFF0CC++\u6A21\u677F\u66F4\u5F3A\u5927\u3001\u66F4\u4E00\u81F4\uFF08\u73B0\u5728\u66F4\u5BB9\u6613\u4F7F\u7528\uFF0C\u56E0\u4E3A\u7F16\u8BD1\u5668\u7684\u9519\u8BEF\u6D88\u606F\u662F\u53EF\u4EE5\u5BB9\u5FCD\u7684\uFF09\u3002\u4EBA\u4EEC\u4E00\u76F4\u5728\u8BA4\u771F\u5BF9\u5F85\u7269\u5316\uFF08reification\uFF09\uFF0C\u8FD9\u53EF\u80FD\u662F\u6709\u7528\u7684\u4E1C\u897F\uFF0C\u4F46\u662F\u5728\u90A3\u79CD\u88AB\u4E25\u683C\u7EA6\u675F\u6240\u524A\u5F31\u7684\u8BBE\u8BA1\u4E2D\u5E76\u6CA1\u6709\u591A\u5927\u5F71\u54CD\u3002</p><p>\u8FD9\u6837\u7684\u4F8B\u5B50\u8FD8\u6709\u5F88\u591A\u5F88\u591A\u3002\u8FD9\u662F\u5426\u610F\u5473\u7740Java\u5931\u8D25\u4E86\uFF1F\u7EDD\u5BF9\u4E0D\u3002Java\u5C06\u7A0B\u5E8F\u5458\u7684\u4E3B\u6D41\u5E26\u5165\u4E86\u5783\u573E\u6536\u96C6\u3001\u865A\u62DF\u673A\u548C\u4E00\u81F4\u7684\u9519\u8BEF\u5904\u7406\u6A21\u578B\u7684\u4E16\u754C\u3002\u7531\u4E8E\u5B83\u7684\u6240\u6709\u7F3A\u9677\uFF0C\u5B83\u5C06\u6211\u4EEC\u63D0\u5347\u5230\u4E86\u4E00\u4E2A\u6C34\u5E73\uFF0C\u73B0\u5728\u6211\u4EEC\u5DF2\u7ECF\u51C6\u5907\u597D\u4F7F\u7528\u66F4\u9AD8\u7EA7\u522B\u7684\u8BED\u8A00\u4E86\u3002</p><p>\u6709\u4E00\u70B9\uFF0CC++\u662F\u9886\u5148\u7684\u8BED\u8A00\uFF0C\u4EBA\u4EEC\u8BA4\u4E3A\u5B83\u603B\u662F\u5982\u6B64\u3002\u8BB8\u591A\u4EBA\u5BF9Java\u6709\u540C\u6837\u7684\u770B\u6CD5\uFF0C\u4F46\u7531\u4E8EJVM\uFF0CJava\u4F7F\u5F97\u53D6\u4EE3\u81EA\u5DF1\u53D8\u5F97\u66F4\u52A0\u5BB9\u6613\u3002\u73B0\u5728\u6709\u53EF\u80FD\u4F1A\u6709\u4EBA\u521B\u5EFA\u4E00\u79CD\u65B0\u8BED\u8A00\uFF0C\u5E76\u4F7F\u5176\u5728\u77ED\u65F6\u95F4\u5185\u50CFJava\u4E00\u6837\u9AD8\u6548\u8FD0\u884C\u3002\u4EE5\u524D\uFF0C\u4E3A\u65B0\u8BED\u8A00\u5F00\u53D1\u4E00\u4E2A\u6B63\u786E\u6709\u6548\u7684\u7F16\u8BD1\u5668\u9700\u8981\u82B1\u8D39\u5927\u90E8\u5206\u5F00\u53D1\u65F6\u95F4\u3002</p><p>\u8FD9\u79CD\u60C5\u51B5\u5DF2\u7ECF\u53D1\u751F\u4E86\uFF0C\u5305\u62EC\u50CFScala\u8FD9\u6837\u7684\u9AD8\u7EA7\u9759\u6001\u8BED\u8A00\uFF0C\u4EE5\u53CA\u52A8\u6001\u8BED\u8A00\uFF0C\u65B0\u7684\u4E14\u53EF\u79FB\u690D\u7684\uFF0C\u5982Groovy\uFF0CClojure\uFF0CJRuby\u548CJython\u3002\u8FD9\u662F\u672A\u6765\uFF0C\u5E76\u4E14\u8FC7\u6E21\u5F88\u987A\u7545\uFF0C\u56E0\u4E3A\u53EF\u4EE5\u5F88\u8F7B\u6613\u5730\u5C06\u8FD9\u4E9B\u65B0\u8BED\u8A00\u4E0E\u73B0\u6709Java\u4EE3\u7801\u7ED3\u5408\u4F7F\u7528\uFF0C\u5E76\u4E14\u5FC5\u8981\u65F6\u53EF\u4EE5\u91CD\u5199\u90A3\u4E9B\u5728Java\u4E2D\u7684\u74F6\u9888\u3002</p><p>\u5728\u64B0\u5199\u672C\u6587\u65F6\uFF0CJava\u662F\u4E16\u754C\u4E0A\u7684\u5934\u53F7\u7F16\u7A0B\u8BED\u8A00\u3002\u7136\u800C\uFF0CJava\u6700\u7EC8\u5C06\u4F1A\u51CF\u5F31\uFF0C\u5C31\u50CFC++\u4E00\u6837\uFF0C\u6CA6\u53EA\u5728\u7279\u6B8A\u60C5\u51B5\u4E0B\u4F7F\u7528\uFF08\u6216\u8005\u53EA\u662F\u7528\u6765\u652F\u6301\u4F20\u7EDF\u7684\u4EE3\u7801\uFF0C\u56E0\u4E3A\u5B83\u4E0D\u80FD\u50CFC++\u90A3\u6837\u548C\u786C\u4EF6\u8FDE\u63A5\uFF09\u3002\u4F46\u662F\u65E0\u610F\u4E2D\u7684\u597D\u5904\uFF0C\u4E5F\u662FJava\u771F\u6B63\u610F\u5916\u7684\u5149\u5F69\u4E4B\u5904\u5728\u4E8E\u5B83\u4E3A\u81EA\u5DF1\u7684\u66FF\u4EE3\u54C1\u521B\u9020\u4E86\u4E00\u6761\u975E\u5E38\u7545\u901A\u7684\u9053\u8DEF\uFF0C\u5373\u4F7FJava\u672C\u8EAB\u5DF2\u7ECF\u8FBE\u5230\u4E86\u65E0\u6CD5\u518D\u53D1\u5C55\u7684\u7A0B\u5EA6\u3002\u672A\u6765\u6240\u6709\u7684\u8BED\u8A00\u90FD\u5E94\u8BE5\u4ECE\u4E2D\u5B66\u4E60\uFF1A\u8981\u4E48\u521B\u5EFA\u4E00\u4E2A\u53EF\u4EE5\u91CD\u6784\u7684\u6587\u5316\uFF08\u50CFPython\u548CRuby\u505A\u7684\u90A3\u6837\uFF09\uFF0C\u8981\u4E48\u5C31\u8BA9\u7ADE\u4E89\u8005\u8301\u58EE\u6210\u957F\u3002</p><div style="page-break-after:always;"></div>',15),r=[v];function i(c,s){return e(),p("div",null,r)}var n=a(o,[["render",i],["__file","Appendix-The-Positive-Legacy-of-C-plus-plus-and-Java.html.vue"]]);export{n as default};
